  `v_shot` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '客队投篮出手数',
  `v_shotin` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '客队投篮进球数',
  `v_tree` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '客队三分投篮数',
  `v_treein` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '客队三分进球数',
  `v_pen` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队总罚球数',
  `v_penin` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队罚球进球数',
  `v_be_re` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队前篮板数',
  `v_be_af` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队后篮板数',
  `v_assist` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队助攻数',
  `v_steal` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队抢断数',
  `v_block` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队盖帽数',
  `v_error` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队失误数',
  `v_foul` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队犯规数',
  `v_score` smallint(10) unsigned NOT NULL DEFAULT '0' COMMENT '主队得分数',
  `end` enum('N','Y') NOT NULL DEFAULT 'N' COMMENT '比赛是否结束',
